import * as React from 'react';
import { useQuery, useMutation } from 'react-apollo';
import renderComponent from '../utils/renderComponent';
import { getAllPostsQuery } from './queries/getAllPosts';
import { voteForPost } from './mutations/voteForPost';

function PostsIndex() {
  const { data, loading, error } = useQuery(getAllPostsQuery);
  const [vote] = useMutation(voteForPost);

  if (loading) return 'Loading...';
  if (error) return `Error! ${error.message}`;

  const handleUpvote = (id) => {
    if(!data.viewer) {
      window.location.href += "users/sign_up";
    } else {
      vote({ variables: { id: id }});
    }
  }

  return (
    <div className="box">
      {data.postsAll.map((post) => (
        <article className="post" key={post.id}>
          <h2>
            <a href={`/posts/${post.id}`}>{post.title}</a>
          </h2>
          <div className="url">
            <a href={post.url}>{post.url}</a>
          </div>
          <div className="tagline">{post.tagline}</div>
          <footer>
            <button onClick={handleUpvote.bind(this, post.id)}>ðŸ”¼ 0 </button>
            <button>ðŸ’¬ {post.commentsCount}</button>
          </footer>
        </article>
      ))}
    </div>
  );
}

renderComponent(PostsIndex);
