import React, { useEffect, useState } from 'react';
import gql from 'graphql-tag';
import { useQuery } from 'react-apollo';
import renderComponent from '../utils/renderComponent';

import { Header } from './components/Header';
import { List } from './components/List';
import { Comments } from './components/Comments';
import { About } from './components/About';
import { LatestLaunches } from './components/LatestLaunches';

const QUERY = gql`
  query PostsPage {
    viewer {
      id
    }
    postsAll {
      id
      description
      title
      tagline
      votesCount
      url
      commentsCount
      dailyFeedPosition
      weeklyFeedPosition
      image(width: 60, height: 60)
      user {
        id
        name
        image(height: 50, width: 50)
      }
      makers {
        image(height: 50, width: 50)
      }
      commenters {
        image(height: 50, width: 50)
      }
      comments {
        id
        text
        user {
          name
          username
          image(height: 50, width: 50)
        }
      }
    }
  }
`;

function PostsShow({ postId }) {
  const { data, loading, error } = useQuery(QUERY);
  const [post, setPost] = useState(null);

  useEffect(() => {
    if(data && !loading) {
      setPost(data.postsAll.filter((item) => item.id == postId)[0]);
    }
  },[data,loading]);

  console.log(post);

  if(loading) {
    return <div className="box">Loading...</div>
  }

  if(error) {
    return <div className="box">There is an error on the page: <br/> {error.message}</div>
  }

  const headerProps = {
    description: post?.description,
    tagLine: post?.tagLine,
    title: post?.title,
    image: post?.image,
    url: post?.url
  }

  return (
    <div className="box">
      <Header { ...headerProps } />
      <List post={post} />
      <Comments post={post} />
      <About post={post} />
      <LatestLaunches />
    </div>
  );
}

renderComponent(PostsShow);
