import React, { useEffect, useState } from 'react';
import gql from 'graphql-tag';
import { useQuery } from 'react-apollo';
import renderComponent from './utils/renderComponent';

const QUERY = gql`
  query PostsPage {
    viewer {
      id
    }
    postsAll {
      id
      title
      tagline
      url
      commentsCount
    }
  }
`;

function PostsShow({ postId }) {
  const { data, loading, error } = useQuery(QUERY);
  const [post, setPost] = useState(null);

  useEffect(() => {
    if(data && !loading) {
     setPost(data.filter((item) => item.id === postId)[0]);
    }
  },[data, loading]);

  console.log(post);

  return (
    <>
      <div className="box">
        <strong>TODO: Show info about post with id {postId}.</strong>
        <br />
        <em>Find js file at "app/javascript/packs/posts-show.jsx"</em>
      </div>
      <div className="box">
        <article className="post">
          <h2>
            <a href={`/posts/${post.id}`}>{post.title}</a>
          </h2>
          <div className="url">
            <a href={post.url} target="_blank">
              {post.url}
            </a>
          </div>
          <div className="tagline">{post.tagline}</div>
          <footer>
            <button>ðŸ”¼ 0 </button> {post.commentsCount} comments | author:{' '}
            {post.user.name}
          </footer>
        </article>
      </div>
      <div className="box">
        <strong>TODO: Show post comments</strong>
      </div>
    </>
  );
}

renderComponent(PostsShow);
