import React, { useState, useEffect } from 'react';
import userImg from './assets/hunter.svg';
import upvoterImg from './assets/upvoter.svg';
import makerImg from './assets/maker.svg';
import commenterImg from './assets/commenter.svg';

export const List = ({ post }) => {
  const [list, setList] = useState([]);

  useEffect(() => {
    if(post?.commenters) {
      setList([
        {...post.user, icon: userImg}, 
        ...post.commenters.map(item => ({...item, icon: makerImg })), // Moked makers
        ...post.commenters.map(item => ({...item, icon: commenterImg })),
        ...post.commenters.map(item => ({...item, icon: upvoterImg })) // Moked upvoters
      ])
    }
  }, [post]);

    return (
        <div className="list">
          {list.map((item, index) => 
            <div className="avatarContainer">
              <img key={index} className="avatars" src={item.image} />
              <img src={item.icon} className="avatarIcon" />
            </div>
          )}
        </div>
    )
}
