import React, { useState, useEffect } from 'react';

export const List = ({ post }) => {
  const [list, setList] = useState([]);

  useEffect(() => {
    if(post?.commenters) {
      setList([...post.commenters])
    }
  }, [post]);

    return (
        <div className="list">
          {list.map((item, index) => 
            <div className="avatarContainer">
              <img key={index} className="avatars" src={item.image} />
            </div>
          )}
        </div>
    )
}
