import React from 'react';

export const Comments = ({ post }) => {
    return (
        <div className="comments">
          {post?.comments.map((item) => (
            <div className="commentItem">
              <div className="avatar"><img src={item.user.image} /></div>
              <div className="main">{item.text}</div>
            </div>
          ))}
        </div>
    )
}
